namespace spec\LdapTools\Security;

use LdapTools\Security\Ace\Ace;
use LdapTools\Security\Ace\AceType;
use LdapTools\Security\Acl\Dacl;
use LdapTools\Security\Acl\Sacl;
use LdapTools\Security\ControlFlags;
use LdapTools\Security\SecurityDescriptor;
use LdapTools\Security\SID;
use PhpSpec\ObjectBehavior;

class SecurityDescriptorSpec extends ObjectBehavior
{
    protected $expectedSddl;

    function let()
    {
        $this->beConstructedWith(hex2bin('010004801400000020000000000000002c00000001010000000000050a00000001010000000000050a00000004001c0001000000000214000100020001010000000000050a000000'));
    }

    function it_is_initializable()
    {
        $this->shouldHaveType(SecurityDescriptor::class);
    }

    function it_should_get_the_flags_that_are_set()
    {
        $this->getControlFlags()->getValue()->shouldBeEqualTo(32772);
        $this->getControlFlags()->GetShortNames()->shouldBeEqualTo(['SR', 'DP']);
    }

    function it_should_have_a_revision_of_1()
    {
        $this->getRevision()->shouldBeEqualTo(1);
    }

    function it_should_have_a_resource_manager_control_flag_of_0()
    {
        $this->getRmControlFlags()->getValue()->shouldBeEqualTo(0);
    }

    function it_should_have_the_owner_SID_set_correctly()
    {
        $this->getOwner()->toString()->shouldBeEqualTo('S-1-5-10');
    }

    function it_should_have_the_group_SID_set_correctly()
    {
        $this->getGroup()->toString()->shouldBeEqualTo('S-1-5-10');
    }

    function it_should_get_the_dacl_when_it_is_set()
    {
        $this->getDacl()->shouldHaveType('LdapTools\Security\Acl\Dacl');
    }

    function it_should_set_the_DACL()
    {
        $this->setDacl(new Dacl())->getControlFlags()->has(ControlFlags::FLAG['DACL_PRESENT'])->shouldBeEqualTo(true);
        $this->setDacl(null)->getControlFlags()->has(ControlFlags::FLAG['DACL_PRESENT'])->shouldBeEqualTo(false);
    }

    function it_should_return_null_for_the_sacl_when_it_is_not_present()
    {
        $this->getSacl()->shouldBeNull();
    }

    function it_should_set_the_SACL()
    {
        $this->setSacl(new Sacl())->getControlFlags()->has(ControlFlags::FLAG['SACL_PRESENT'])->shouldBeEqualTo(true);
        $this->setSacl(null)->getControlFlags()->has(ControlFlags::FLAG['SACL_PRESENT'])->shouldBeEqualTo(false);
    }

    function it_should_get_the_revision_level_of_the_ACL()
    {
        $this->getDacl()->getRevision()->shouldBeEqualTo(4);
    }

    function it_should_have_one_ACE_on_the_ACL()
    {
        $this->getDacl()->getAces()->shouldHaveCount(1);
    }

    function it_should_get_the_binary_representation()
    {
        $this->toBinary()->shouldBeEqualTo(hex2bin('010004801400000020000000000000002c00000001010000000000050a00000001010000000000050a00000004001c0001000000000214000100020001010000000000050a000000'));
    }

